import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";
import CDKSpinner from "./CDKSpinner.gif";

const PageSpinner = ({ show, className, ...other }) => (
  <div
    className={classnames(
      "cdk-page-spinner",
      "cdk-page-spinner--overlay",
      {
        "cdk-page-spinner--hidden": !show
      },
      className
    )}
    {...other}
  >
    <img className="cdk-page-spinner__logo" src={CDKSpinner} alt="" />
  </div>
);

PageSpinner.propTypes = {
  /** Show the spinner. */
  show: PropTypes.bool
};

PageSpinner.defaultProps = {
  show: false
};

export default PageSpinner;
