import React from "react";
import { PageSpinner } from ".";
import renderer from "react-test-renderer";

it("renders the spinner", () => {
  expect(renderer.create(<PageSpinner show />)).toMatchSnapshot();
});

it("hides the spinner", () => {
  expect(renderer.create(<PageSpinner />)).toMatchSnapshot();
});

it("passes props through", () => {
  expect(
    renderer.create(<PageSpinner className="custom" style={{ zIndex: 20 }} />)
  ).toMatchSnapshot();
});
