import React from "react";
import classnames from "classnames";

/**
 * Simple container that uses theme background color.
 * Can be used to host cards and elevations if you have
 * a page with a non-theme background color.
 */
const Paper = ({ className, children, ...other }) => (
  <div className={classnames("paper", className)} {...other}>
    {children}
  </div>
);

export default Paper;
