import React from "react";
import PropTypes from "prop-types";
import { List, ListItem } from "@cdk-uip/react-list";
import { Icon } from "@cdk-uip/react-icon";

const PersistentSearchResults = ({ results, onClick }) => (
  <List>
    {results.map((result, index) => (
      <ListItem
        className="cdk-persistent-search__results"
        key={index}
        onMouseDown={() => {
          onClick(result, index);
        }}
      >
        <Icon className="cdk-persistent-search__watch-icon">query_builder</Icon>
        {result}
      </ListItem>
    ))}
  </List>
);

PersistentSearchResults.propTypes = {
  results: PropTypes.arrayOf(PropTypes.string),
  onClick: PropTypes.func
};

PersistentSearchResults.defaultProps = {
  results: [],
  onClick: (result, index) => {}
};

export default PersistentSearchResults;
