import React from "react";
import classnames from "classnames";

/**
 * Use as a controlled component (supply `checked` and `onChange` props) or
 * with a `RadioGroup` component.
 *
 * For a label, use the `<FormField>` component.
 */
const Radio = ({ className, ...other }) => (
  <div className={classnames("mdc-radio", className)}>
    <input className="mdc-radio__native-control" type="radio" {...other} />
    <div className="mdc-radio__background">
      <div className="mdc-radio__outer-circle" />
      <div className="mdc-radio__inner-circle" />
    </div>
  </div>
);

export default Radio;
