import React from "react";
import ReactDOM from "react-dom";
import { Radio, RadioGroup } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <RadioGroup
        name="office"
        value="H"
        className="custom"
        style={{ background: "pink" }}
      >
        <Radio value="H">Hungerford</Radio>
        <Radio value="P">Portland</Radio>
        <Radio value="C">Chicago</Radio>
      </RadioGroup>
    )
  ).toMatchSnapshot();
});

it("mounts and unmounts", () => {
  const div = document.createElement("div");
  ReactDOM.render(<Radio />, div);
  ReactDOM.unmountComponentAtNode(div);
});

it("renders radio disabled", () => {
  expect(
    renderer.create(
      <RadioGroup name="office" value="H">
        <Radio value="H">Hungerford</Radio>
        <Radio value="P" disabled>
          Portland
        </Radio>
        <Radio value="C">Chicago</Radio>
      </RadioGroup>
    )
  ).toMatchSnapshot();
});

it("renders RadioGroup disabled", () => {
  expect(
    renderer.create(
      <RadioGroup name="office" value="H" disabled>
        <Radio value="H">Hungerford</Radio>
        <Radio value="P" disabled>
          Portland
        </Radio>
        <Radio value="C">Chicago</Radio>
      </RadioGroup>
    )
  ).toMatchSnapshot();
});
