import React from "react";
import { Ripple } from ".";
import { storiesOf } from "@storybook/react";

const Component = () => (
  <div
    style={{
      textAlign: "center",
      cursor: "default",
      height: "300px",
      lineHeight: "300px"
    }}
  >
    Click for a ripple!
  </div>
);

storiesOf("ripple", module)
  .add("normal", () => (
    <Ripple>
      <Component />
    </Ripple>
  ))
  .add("primary", () => (
    <Ripple primary>
      <Component />
    </Ripple>
  ))
  .add("accent", () => (
    <Ripple accent>
      <Component />
    </Ripple>
  ));
