import React from "react";
import ReactDOM from "react-dom";
import { Ripple } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <Ripple>
        <p>Ripple</p>
      </Ripple>
    )
  ).toMatchSnapshot();
});

it("renders primary", () => {
  expect(
    renderer.create(
      <Ripple primary>
        <p>Ripple</p>
      </Ripple>
    )
  ).toMatchSnapshot();
});

it("renders accent", () => {
  expect(
    renderer.create(
      <Ripple accent>
        <p>Ripple</p>
      </Ripple>
    )
  ).toMatchSnapshot();
});

it("mounts", () => {
  const div = document.createElement("div");
  ReactDOM.render(
    <Ripple>
      <p>Ripple</p>
    </Ripple>,
    div
  );
  ReactDOM.unmountComponentAtNode(div);
});
