import React from "react";
import ReactDOM from "react-dom";
import { Slider } from ".";
import renderer from "react-test-renderer";

it("renders continuous", () => {
  expect(
    renderer.create(<Slider min={100} max={500} step={25} value={200} />)
  ).toMatchSnapshot();
});

it("renders discrete", () => {
  expect(
    renderer.create(
      <Slider discrete min={100} max={500} step={25} value={200} />
    )
  ).toMatchSnapshot();
});

it("mounts and unmounts", () => {
  const div = document.createElement("div");
  ReactDOM.render(<Slider min={100} max={500} step={25} value={200} />, div);
  ReactDOM.render(<Slider min={100} max={500} step={25} value={300} />, div);
  ReactDOM.unmountComponentAtNode(div);
});
