import React from "react";
import { FAB } from "@cdk-uip/react-fab";
import { string } from "prop-types";

const SpeedDialItem = ({ icon, exited, ...other }) => (
  <li {...other}>
    <FAB mini exited={exited}>
      {icon}
    </FAB>
  </li>
);

SpeedDialItem.propTypes = {
  /** Icon */
  icon: string
};

export default SpeedDialItem;
