import React from "react";
import classnames from "classnames";
import { oneOf } from "prop-types";

/**
 * Block status indicator.
 *
 * Available types:
 *
 * - Inactive - Use when status isn't available or isn't relevant.
 * - Informational - Use only if status isn't positive or negative.
 * - Positive - Use if information presented is completely positive.
 * - Warning - Can be used as either an alert (neither positive or negative) or if the information is slightly negative.
 * - Negative - Use if information is completely negative.
 */
const StatusIndicatorBlock = ({ className, type, children, ...other }) => (
  <div
    className={classnames(
      "cdk-status-indicator",
      {
        [`cdk-status-indicator--block__${type}`]: type
      },
      className
    )}
    {...other}
  >
    <span className="cdk-status-indicator--block__text">{children}</span>
  </div>
);

StatusIndicatorBlock.propTypes = {
  /** Type */
  type: oneOf(["inactive", "positive", "informational", "warning", "negative"])
};

StatusIndicatorBlock.defaultProps = {
  type: "inactive"
};

export default StatusIndicatorBlock;
