import React from "react";
import { StatusIndicatorBlock } from ".";
import renderer from "react-test-renderer";

it("renders inactive", () => {
  expect(
    renderer.create(
      <StatusIndicatorBlock type="inactive">Text</StatusIndicatorBlock>
    )
  ).toMatchSnapshot();
});

it("renders positive", () => {
  expect(
    renderer.create(
      <StatusIndicatorBlock type="positive">Text</StatusIndicatorBlock>
    )
  ).toMatchSnapshot();
});

it("renders informational", () => {
  expect(
    renderer.create(
      <StatusIndicatorBlock type="informational">Text</StatusIndicatorBlock>
    )
  ).toMatchSnapshot();
});

it("renders warning", () => {
  expect(
    renderer.create(
      <StatusIndicatorBlock type="warning">Text</StatusIndicatorBlock>
    )
  ).toMatchSnapshot();
});

it("renders negative", () => {
  expect(
    renderer.create(
      <StatusIndicatorBlock type="negative">Text</StatusIndicatorBlock>
    )
  ).toMatchSnapshot();
});
