import React from "react";
import { StatusIndicatorInline } from ".";
import renderer from "react-test-renderer";

it("renders inactive", () => {
  expect(
    renderer.create(
      <StatusIndicatorInline type="inactive">Text</StatusIndicatorInline>
    )
  ).toMatchSnapshot();
});

it("renders positive", () => {
  expect(
    renderer.create(
      <StatusIndicatorInline type="positive">Text</StatusIndicatorInline>
    )
  ).toMatchSnapshot();
});

it("renders informational", () => {
  expect(
    renderer.create(
      <StatusIndicatorInline type="informational">Text</StatusIndicatorInline>
    )
  ).toMatchSnapshot();
});

it("renders warning", () => {
  expect(
    renderer.create(
      <StatusIndicatorInline type="warning">Text</StatusIndicatorInline>
    )
  ).toMatchSnapshot();
});

it("renders negative", () => {
  expect(
    renderer.create(
      <StatusIndicatorInline type="negative">Text</StatusIndicatorInline>
    )
  ).toMatchSnapshot();
});
