import React from "react";
import { HorizontalLinearStepper } from ".";
import renderer from "react-test-renderer";

it("renders with the first step active", () => {
  expect(
    renderer.create(
      <HorizontalLinearStepper
        activeStep={0}
        steps={["Step 1", "Step 2", "Step 3"]}
      />
    )
  ).toMatchSnapshot();
});

it("renders with the second step active", () => {
  expect(
    renderer.create(
      <HorizontalLinearStepper
        activeStep={1}
        steps={["Step 1", "Step 2", "Step 3"]}
      />
    )
  ).toMatchSnapshot();
});

it("renders completed", () => {
  expect(
    renderer.create(
      <HorizontalLinearStepper
        activeStep={2}
        finished
        steps={["Step 1", "Step 2", "Step 3"]}
      />
    )
  ).toMatchSnapshot();
});

it("renders using spans", () => {
  expect(
    renderer.create(
      <HorizontalLinearStepper
        activeStep={0}
        steps={[<span>Step 1</span>, <span>Step 2</span>, <span>Step 3</span>]}
      />
    )
  ).toMatchSnapshot();
});
