import React from "react";
import classnames from "classnames";
import { bool } from "prop-types";

/**
 * Use in conjunction with a `<Stepper>` component to represent an
 * individual step.
 */
const Step = ({ inactive, editable, className, children, ...other }) => (
  <li
    className={classnames(
      "cdk-stepper__step",
      {
        "cdk-stepper__step--inactive": inactive,
        "cdk-stepper__step--editable": editable,
        "mdc-ripple-surface": editable
      },
      className
    )}
    {...other}
  >
    {children}
  </li>
);

Step.propTypes = {
  /** Renders the step as inactive. */
  inactive: bool,
  /** Make the step clickable, with a ripple effect on supported browsers. */
  editable: bool
};

export default Step;
