import React from "react";
import StepIcon from "./StepIcon";
import { Icon } from "@cdk-uip/react-icon";

/**
 * Displays a tick icon. Use for steps that have been completed.
 *
 * This is a shortcut for:
 *
 * ```js
 * <StepIcon>
 *   <Icon>done</Icon>
 * </StepIcon>
 * ```
 */
const StepIconCompleted = props => (
  <StepIcon {...props}>
    <Icon>done</Icon>
  </StepIcon>
);

export default StepIconCompleted;
