import React from "react";
import StepIcon from "./StepIcon";
import { Icon } from "@cdk-uip/react-icon";

/**
 * Displays an edit icon. Use for steps that can be modified.
 *
 * This is a shortcut for:
 *
 * ```js
 * <StepIcon>
 *   <Icon>edit</Icon>
 * </StepIcon>
 * ```
 */
const StepIconEditable = props => (
  <StepIcon {...props}>
    <Icon>edit</Icon>
  </StepIcon>
);

export default StepIconEditable;
