import React from "react";
import classnames from "classnames";

/**
 * Display a step label.
 *
 * Use with a text child for a simple label, e.g.:
 *
 * ```js
 * <StepLabel>The label</StepLabel>
 * ```
 *
 * For labels with optional text you can use `<StepLabelText>` and
 * `<StepLabelSecondaryText>`.
 */
const StepLabel = ({ className, children, ...other }) => (
  <div className={classnames("cdk-stepper__step__label", className)} {...other}>
    {children}
  </div>
);

export default StepLabel;
