import React from "react";
import classnames from "classnames";

/**
 * Used to display optional text for a step. For example:
 *
 * ```js
 * <StepLabel>
 *   <StepLabelText>The main label</StepLabel>
 *   <StepLabelSecondaryText>Optional</StepLabelSecondaryText>
 * </StepLabel>
 * ```
 */
const StepLabelSecondaryText = ({ className, children, ...other }) => (
  <div
    className={classnames("cdk-stepper__step__label__secondary", className)}
    {...other}
  >
    {children}
  </div>
);

export default StepLabelSecondaryText;
