import React from "react";
import classnames from "classnames";

/**
 * The primary label for a step. You can use this component in
 * conjunction with `<StepLabelSecondaryText>` to add primary and
 * optional labels.
 */
const StepLabelText = ({ className, children, ...other }) => (
  <div
    className={classnames("cdk-stepper__step__label__text", className)}
    {...other}
  >
    {children}
  </div>
);

export default StepLabelText;
