import React from "react";
import classnames from "classnames";

/**
 * Steppers display progress through a sequence of logical and numbered steps.
 * They may also be used for navigation.
 *
 * https://material.io/guidelines/components/steppers.html
 *
 * For a higher-level component that takes care of some of the logic, use
 * `HorizontalLinearStepper`.
 */
const Stepper = ({ className, children, ...other }) => (
  <ul className={classnames("cdk-stepper", className)} {...other}>
    {children}
  </ul>
);

export default Stepper;
