import React from "react";
import {
  Stepper,
  Step,
  StepIcon,
  StepIconCompleted,
  StepIconEditable,
  StepLabel,
  StepLabelText,
  StepLabelSecondaryText,
  StepDivider
} from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <Stepper>
        <Step>
          <StepIconCompleted />
          <StepLabel>Select campaign settings</StepLabel>
        </Step>
        <StepDivider />
        <Step editable>
          <StepIconEditable />
          <StepLabel>
            <StepLabelText>Create an ad group</StepLabelText>
            <StepLabelSecondaryText>Optional</StepLabelSecondaryText>
          </StepLabel>
        </Step>
        <StepDivider />
        <Step inactive>
          <StepIcon>3</StepIcon>
          <StepLabel>Create an ad</StepLabel>
        </Step>
      </Stepper>
    )
  ).toMatchSnapshot();
});
