import React from "react";
import { Switch } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(renderer.create(<Switch />)).toMatchSnapshot();
});

it("renders disabled", () => {
  expect(renderer.create(<Switch disabled />)).toMatchSnapshot();
});

it("renders with a custom class", () => {
  expect(renderer.create(<Switch className="custom" />)).toMatchSnapshot();
});
