/* eslint-disable jsx-a11y/href-no-hash */
import React from "react";
import classnames from "classnames";
import { Icon } from "@cdk-uip/react-icon";
import { MDCTabBarScroller } from "@material/tabs/dist/mdc.tabs";

class TabBarScroller extends React.Component {
  componentDidMount() {
    if (this.scroller) {
      MDCTabBarScroller.attachTo(this.scroller);
    }
  }

  render() {
    const { className, children, ...other } = this.props;
    return (
      <div
        className={classnames("mdc-tab-bar-scroller", className)}
        {...other}
        ref={scroller => {
          this.scroller = scroller;
        }}
      >
        <div className="mdc-tab-bar-scroller__indicator mdc-tab-bar-scroller__indicator--back">
          <Icon className="mdc-tab-bar-scroller__indicator__inner" href="#">
            chevron_left
          </Icon>
        </div>
        <div className="mdc-tab-bar-scroller__scroll-frame">{children}</div>
        <div className="mdc-tab-bar-scroller__indicator mdc-tab-bar-scroller__indicator--forward">
          <Icon className="mdc-tab-bar-scroller__indicator__inner" href="#">
            chevron_right
          </Icon>
        </div>
      </div>
    );
  }
}

export default TabBarScroller;
