import React from "react";
import { TabBarScroller } from ".";
import { TabBar, Tabs, Tab } from "@cdk-uip/react-tabs";
import { storiesOf } from "@storybook/react";

storiesOf("tab-bar-scroller", module)
  .add("scrollable tabs", () => (
    <TabBarScroller>
      <Tabs scroll>
        {[...Array(10).keys()].map(index => (
          <Tab key={index}>{`Tab ${index + 1}`}</Tab>
        ))}
      </Tabs>
    </TabBarScroller>
  ))
  .add("scrollable tabbar", () => (
    <TabBarScroller>
      <TabBar scroll>
        {[...Array(10).keys()].map(index => (
          <Tab key={index} active={index === 1}>{`Tab ${index + 1}`}</Tab>
        ))}
      </TabBar>
    </TabBarScroller>
  ));
