import React from "react";
import { Table } from ".";
import { Card } from "@cdk-uip/react-card";
import { storiesOf } from "@storybook/react";

const data = [
  "lorem ipsum dolor",
  "sit amet",
  "consectetur adipiscing elit",
  "curabitur",
  "vel hendrerit libero eleifend",
  "blandit nunc ornare",
  "odio ut",
  "orci gravida imperdiet nullam",
  "purus lacinia a",
  "pretium quis",
  "congue"
];

const getRows = ({ rows, cols }) =>
  new Array(rows)
    .fill()
    .map((e, i) => (
      <tr key={i}>
        {new Array(cols)
          .fill()
          .map((e, j) => <td key={j}>{data[(i * cols + j) % data.length]}</td>)}
      </tr>
    ));

storiesOf("table", module)
  .add("Basic", () => (
    <Table>
      <thead>
        <tr>
          <th>One</th>
          <th>Fine</th>
          <th>Table</th>
        </tr>
      </thead>
      <tbody>{getRows({ rows: 3, cols: 3 })}</tbody>
    </Table>
  ))
  .add("In a card", () => (
    <Card>
      <Table>
        <thead>
          <tr>
            <th>One</th>
            <th>Fine</th>
            <th>Table</th>
          </tr>
        </thead>
        <tbody>{getRows({ rows: 7, cols: 3 })}</tbody>
      </Table>
    </Card>
  ))
  .add("Big", () => (
    <Table>
      <thead>
        <tr>
          <th>One</th>
          <th>Fine</th>
          <th>Table</th>
          <th>One</th>
          <th>Fine</th>
          <th>Table</th>
          <th>One</th>
          <th>Fine</th>
          <th>Table</th>
          <th>One</th>
        </tr>
      </thead>
      <tbody>{getRows({ rows: 100, cols: 10 })}</tbody>
    </Table>
  ));
