import React from "react";
import classnames from "classnames";
import { bool } from "prop-types";

const TabBar = ({
  icons,
  iconsWithText,
  scroll,
  className,
  children,
  ...other
}) => (
  <nav
    className={classnames(
      "mdc-tab-bar",
      {
        "mdc-tab-bar--icon-tab-bar": icons,
        "mdc-tab-bar--icons-with-text": iconsWithText,
        "mdc-tab-bar-scroller__scroll-frame__tabs": scroll
      },
      className
    )}
    {...other}
  >
    {children}
    {scroll && <span className="mdc-tab-bar__indicator" />}
  </nav>
);

TabBar.propTypes = {
  /** Icon-only tabs */
  icons: bool,
  /** Display icon and text */
  iconsWithText: bool,
  /** Scrolling support */
  scroll: bool
};

export default TabBar;
