import React from "react";
import { TextArea } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <TextArea id="text" label="Label" rows={4} cols={40} value="Value" />
    )
  ).toMatchSnapshot();
});

it("renders full width", () => {
  expect(
    renderer.create(
      <TextArea
        id="text"
        label="Label"
        rows={4}
        cols={40}
        value="Value"
        fullWidth
      />
    )
  ).toMatchSnapshot();
});

it("renders dense", () => {
  expect(
    renderer.create(
      <TextArea
        id="text"
        label="Label"
        rows={4}
        cols={40}
        value="Value"
        dense
      />
    )
  ).toMatchSnapshot();
});
