import React from "react";
import { TextField, TextFieldIcon, TextFieldHelperText } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  const onClick = jest.fn();
  expect(
    renderer.create(
      <TextField
        id="textfield"
        value="Hello"
        label="Label"
        helperText={
          <TextFieldHelperText persistent validation>
            Help
          </TextFieldHelperText>
        }
        dense
        box
        leadingIcon={<TextFieldIcon>leading_icon</TextFieldIcon>}
        trailingIcon={
          <TextFieldIcon onClick={onClick}>trailing_icon</TextFieldIcon>
        }
      />
    )
  ).toMatchSnapshot();
});

it("renders a date", () => {
  expect(
    renderer.create(<TextField type="date" label="Label" />)
  ).toMatchSnapshot();
});

it("renders a prefix", () => {
  expect(
    renderer.create(<TextField label="Label" value="test" prefix="$" />)
  ).toMatchSnapshot();
});
it("renders without box focus", () => {
  expect(renderer.create(<TextField boxFocus={false} />)).toMatchSnapshot();
});

it("renders outlined", () => {
  expect(
    renderer.create(<TextField value="Hello" label="Label" outlined />)
  ).toMatchSnapshot();
});

it("floats the label for numerics", () => {
  expect(
    renderer.create(<TextField type="number" value={0} label="Number" />)
  ).toMatchSnapshot();
});

it("floats the label for time", () => {
  expect(
    renderer.create(<TextField type="time" label="Time" />)
  ).toMatchSnapshot();
});

it("renders readOnly", () => {
  expect(renderer.create(<TextField readOnly />)).toMatchSnapshot();
});
