import React from "react";
import classnames from "classnames";
import { Icon } from "@cdk-uip/react-icon";
import { func } from "prop-types";

/**
 * Used to render icons in a `<TextField>` component.
 */
const TextFieldIcon = ({
  className,
  tabIndex,
  onClick,
  children,
  ...other
}) => {
  return (
    <Icon
      className={classnames("mdc-text-field__icon", className)}
      onClick={onClick}
      tabIndex={tabIndex === undefined ? (onClick ? "0" : undefined) : tabIndex}
      {...other}
    >
      {children}
    </Icon>
  );
};

TextFieldIcon.propTypes = {
  /** Click handler. Supply a function to make the icon clickable. */
  onClick: func
};

export default TextFieldIcon;
