import React from "react";
import { func } from "prop-types";

TextFieldPrefix.propTypes = {
  prefixRef: func
};

export default function TextFieldPrefix(props) {
  return (
    <span
      className="mdc-text-field__prefix"
      ref={ref => {
        props.prefixRef(ref);
      }}
    >
      <span className="mdc-text-field__prefix__content">{props.children}</span>
    </span>
  );
}
