import React from "react";
import { NewTextArea } from "@cdk-uip/react-text-area";

export default class TextArea extends React.Component {
  componentDidMount() {
    console.warning(
      "@cdk-uip/react-textarea is deprecated and will be removed in v5. Please use @cdk-uip/react-text-area."
    );
  }

  render() {
    return <NewTextArea {...this.props} />;
  }
}
