import React from "react";
import { Theme } from ".";
import { Icon } from "@cdk-uip/react-icon";
import { storiesOf } from "@storybook/react";
import "./demo.css";

storiesOf("theme", module)
  .add("theme colors as text", () => {
    const Wrapper = ({ types }) => (
      <div className="demo-theme-container">
        {types.map((type, index) => (
          <Theme key={index} text={type} background="background">
            <div className="demo-theme-item">{`text="${type}"`}</div>
          </Theme>
        ))}
      </div>
    );
    return <Wrapper types={["primary", "secondary"]} />;
  })
  .add("theme colors as background", () => {
    const Wrapper = ({ types }) => (
      <div className="demo-theme-container">
        {types.map((type, index) => (
          <Theme key={index} background={type.background} text={type.text}>
            <div className="demo-theme-item">
              <div>background="{type.background}"</div>
              <div>text="{type.text}"</div>
            </div>
          </Theme>
        ))}
      </div>
    );
    return (
      <Wrapper
        types={[
          { background: "background", text: "text-primary-on-background" },
          { background: "primary-bg", text: "on-primary" },
          { background: "secondary-bg", text: "on-secondary" }
        ]}
      />
    );
  })
  .add("Text on background", () => (
    <Theme background="background">
      <div className="demo-theme-text-styles">
        <Theme text="text-primary-on-background">Primary</Theme>
        <Theme text="text-secondary-on-background">Secondary</Theme>
        <Theme text="text-hint-on-background">Hint</Theme>
        <Theme text="text-disabled-on-background">Disabled</Theme>
        <Theme text="text-icon-on-background">
          <Icon>favorite</Icon>
        </Theme>
      </div>
    </Theme>
  ))
  .add("Text on primary", () => (
    <Theme background="primary-bg">
      <div className="demo-theme-text-styles">
        <Theme text="on-primary">Text</Theme>
        <Theme text="on-primary">
          <Icon>favorite</Icon>
        </Theme>
      </div>
    </Theme>
  ))
  .add("Text on secondary", () => (
    <Theme background="secondary-bg">
      <div className="demo-theme-text-styles">
        <Theme text="on-secondary">Text</Theme>
        <Theme text="on-secondary">
          <Icon>favorite</Icon>
        </Theme>
      </div>
    </Theme>
  ))
  .add("Text on light background", () => (
    <div className="demo-theme-light">
      <div className="demo-theme-text-styles">
        <Theme text="text-primary-on-light">Primary</Theme>
        <Theme text="text-secondary-on-light">Secondary</Theme>
        <Theme text="text-hint-on-light">Hint</Theme>
        <Theme text="text-disabled-on-light">Disabled</Theme>
        <Theme text="text-icon-on-light">
          <Icon>favorite</Icon>
        </Theme>
      </div>
    </div>
  ))
  .add("Text on dark background", () => (
    <div className="demo-theme-dark">
      <div className="demo-theme-text-styles">
        <Theme text="text-primary-on-dark">Primary</Theme>
        <Theme text="text-secondary-on-dark">Secondary</Theme>
        <Theme text="text-hint-on-dark">Hint</Theme>
        <Theme text="text-disabled-on-dark">Disabled</Theme>
        <Theme text="text-icon-on-dark">
          <Icon>favorite</Icon>
        </Theme>
      </div>
    </div>
  ));
