import React from "react";
import { Theme } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <Theme text="primary" background="primary-bg">
        Test
      </Theme>
    )
  ).toMatchSnapshot();
});

it("renders a div", () => {
  expect(
    renderer.create(
      <Theme text="primary" background="primary-bg" element="div">
        Test
      </Theme>
    )
  ).toMatchSnapshot();
});

it("injects classes", () => {
  expect(
    renderer.create(
      <Theme text="primary" background="primary-bg">
        <div>Component 1</div>
        <div>Component 2</div>
      </Theme>
    )
  ).toMatchSnapshot();
});

it("ignores null elements", () => {
  expect(
    renderer.create(
      <Theme text="primary" background="primary-bg" element="div">
        <div>Component</div>
        {null}
      </Theme>
    )
  ).toMatchSnapshot();
});
