import React from "react";
import { ToggleFilter } from ".";
import renderer from "react-test-renderer";

it("renders unchecked", () => {
  expect(
    renderer.create(<ToggleFilter id="test">Label</ToggleFilter>)
  ).toMatchSnapshot();
});

it("renders checked", () => {
  expect(
    renderer.create(
      <ToggleFilter id="test" checked>
        Label
      </ToggleFilter>
    )
  ).toMatchSnapshot();
});

it("renders disabled", () => {
  expect(
    renderer.create(
      <ToggleFilter id="test" disabled>
        Label
      </ToggleFilter>
    )
  ).toMatchSnapshot();
});
