import React, { Fragment } from "react";
import classnames from "classnames";
import { bool } from "prop-types";
import ToolbarSpacer from "./ToolbarSpacer";

/**
 * You can fix the toolbar to the top using the `fixed` prop. When doing this you can
 * ensure your content gets padded automatically using the `adjust` prop which will
 * insert a `<div>` behind the toolbar that is the correct height.
 *
 * ```js
 * <Toolbar fixed adjust>...</Toolbar>
 * ```
 *
 * Alternatively wrap your conntent in an element that has the `mdc-toolbar-fixed-adjust`
 * class.
 *
 * ```js
 * <div>
 *  <Toolbar fixed>
 *    <ToolbarRow>
 *      <ToolbarSection>
 *        <ToolbarTitle>Toolbar</ToolbarTitle>
 *      </ToolbarSection>
 *    </ToolbarRow>
 *  </Toolbar>
 *
 *  <div className="mdc-toolbar-fixed-adjust">
 *    Main content goes here
 *  </div>
 * </div>
 */
const Toolbar = ({ fixed, adjust, className, children, ...other }) => (
  <Fragment>
    <header
      className={classnames(
        "mdc-toolbar",
        {
          "mdc-toolbar--fixed": fixed
        },
        className
      )}
      {...other}
    >
      {children}
    </header>
    {adjust && <ToolbarSpacer />}
  </Fragment>
);

Toolbar.propTypes = {
  /** Fix the toolbar to the top of the page. */
  fixed: bool,
  /** Auto-adjust content to take a fixed toolbar into account */
  adjust: bool
};

export default Toolbar;
