import React from "react";
import classnames from "classnames";
import { bool } from "prop-types";

/**
 * Offical replacement for the `<Toolbar>` component. Use to supply
 * your app with a navigation icon, title and icon actions.
 */
const TopAppBar = ({ className, dense, fixed, ...other }) => (
  <header
    className={classnames(
      "mdc-top-app-bar",
      {
        "mdc-top-app-bar--dense": dense,
        "mdc-top-app-bar--fixed": fixed,
        "mdc-top-app-bar--fixed-scrolled": fixed
      },
      className
    )}
    {...other}
  />
);

TopAppBar.propTypes = {
  /** Use a dense version of the app bar. */
  dense: bool,
  /** Fix to the top of the page. */
  fixed: bool
};

export default TopAppBar;
