import React from "react";
import {
  TopAppBar,
  TopAppBarRow,
  TopAppBarSection,
  TopAppBarTitle,
  TopAppBarNavigationIcon,
  TopAppBarActionItem
} from ".";
import { storiesOf } from "@storybook/react";
import { withKnobs, boolean } from "@storybook/addon-knobs";

storiesOf("top-app-bar", module)
  .addDecorator(withKnobs)
  .add("props", () => {
    const nav = boolean("nav icon", true);
    const actions = boolean("action items", true);
    return (
      <div style={{ marginLeft: -8, marginTop: -8 }}>
        <TopAppBar
          dense={boolean("dense", false)}
          fixed={boolean("fixed", false)}
        >
          <TopAppBarRow>
            <TopAppBarSection>
              {nav && <TopAppBarNavigationIcon>menu</TopAppBarNavigationIcon>}
              <TopAppBarTitle>Top App Bar Title</TopAppBarTitle>
            </TopAppBarSection>
            {actions && (
              <TopAppBarSection align="end">
                <TopAppBarActionItem>file_download</TopAppBarActionItem>
                <TopAppBarActionItem>print</TopAppBarActionItem>
                <TopAppBarActionItem>bookmark</TopAppBarActionItem>
              </TopAppBarSection>
            )}
          </TopAppBarRow>
        </TopAppBar>
      </div>
    );
  });
