import React from "react";
import classnames from "classnames";
import TopAppBarIcon from "./TopAppBarIcon";

/**
 * An icon action that appears in a `<TopAppBarSection>`.
 */
const TopAppBarActionItem = ({ className, ...other }) => (
  <TopAppBarIcon
    className={classnames("mdc-top-app-bar__action-item", className)}
    {...other}
  />
);

export default TopAppBarActionItem;
