import React from "react";
import classnames from "classnames";
import { oneOf } from "prop-types";

/**
 * An app bar row section.
 *
 * Most app bars consist of two sections: one for the nav icon and
 * app title and another containing the app bar action icons.
 */
const TopAppBarSection = ({ className, align, ...other }) => (
  <section
    className={classnames(
      "mdc-top-app-bar__section",
      { [`mdc-top-app-bar__section--align-${align}`]: align },
      className
    )}
    {...other}
  />
);

TopAppBarSection.propTypes = {
  /** Content alignment */
  align: oneOf(["start", "end"])
};

TopAppBarSection.defaultProps = {
  align: "start"
};

export default TopAppBarSection;
