import React from "react";

// Legacy typography support
const Headline = ({ adjustMargin, children, ...other }) => {
  const style = {
    fontSize: "1.5rem",
    lineHeight: "2rem",
    fontWeight: 400,
    marginTop: adjustMargin ? 0 : undefined
  };
  return (
    <h1 style={style} {...other}>
      {children}
    </h1>
  );
};

export default Headline;
