import React from "react";
import * as T from ".";
import renderer from "react-test-renderer";

test("Typography", () => {
  expect(
    renderer.create(
      <T.Typography element="h1" type="headline1" className="custom">
        Test
      </T.Typography>
    )
  ).toMatchSnapshot();
});

test("Typography defaults", () => {
  expect(
    renderer.create(<T.Typography>Defaults</T.Typography>)
  ).toMatchSnapshot();
});

test("Typography with margins", () => {
  expect(
    renderer.create(
      <T.Typography type="headline1" margins>
        Defaults
      </T.Typography>
    )
  ).toMatchSnapshot();
});

