import React from "react";
import classnames from "classnames";
import { bool, oneOf, string } from "prop-types";

const Typography = ({ element, type, className, margins, ...other }) => {
  const T = element;
  return (
    <T
      className={classnames(
        `mdc-typography--${type}`,
        { "mdc-typography--no-margins": !margins },
        className
      )}
      {...other}
    />
  );
};

Typography.propTypes = {
  /** The element type to render (h1, h2, etc.) */
  element: string,
  /** The MDC type  */
  type: oneOf([
    "headline1",
    "headline2",
    "headline3",
    "headline4",
    "headline5",
    "headline6",
    "subtitle1",
    "subtitle2",
    "body1",
    "body2",
    "caption",
    "button",
    "overline"
  ]),
  /** Custom CSS class */
  className: string,
  /** Remove margins */
  margins: bool
};

Typography.defaultProps = {
  element: "div",
  type: "body1",
  margins: false
};

export default Typography;
