import React, { Fragment } from "react";
import { Typography, Headline, Subheading2, Subheading1, Title } from ".";
import { storiesOf } from "@storybook/react";
import { withKnobs, boolean } from "@storybook/addon-knobs";

storiesOf("typography", module)
  .addDecorator(withKnobs)
  .add("all", () => {
    const data = [
      {
        type: "headline1",
        element: "h1"
      },
      {
        type: "headline2",
        element: "h2"
      },
      {
        type: "headline3",
        element: "h3"
      },
      {
        type: "headline4",
        element: "h4"
      },
      {
        type: "headline5",
        element: "h5"
      },
      {
        type: "headline6",
        element: "h6"
      },
      {
        type: "subtitle1",
        element: "h6"
      },
      {
        type: "subtitle2",
        element: "h6"
      },
      {
        type: "body1",
        element: "p"
      },
      {
        type: "body2",
        element: "p"
      },
      {
        type: "caption",
        element: "span"
      },
      {
        type: "button",
        element: "span"
      },
      {
        type: "overline",
        element: "span"
      }
    ];
    return (
      <div>
        {data.map(({ type, element }) => (
          <div key={type}>
            <Typography
              type={type}
              element={element}
              margins={boolean("margins", false)}
            >
              {type}
            </Typography>
          </div>
        ))}
      </div>
    );
  })
  .add("legacy", () => {
    const adjustMargin = boolean("adjustMargin", false);
    return (
      <Fragment>
        <Headline adjustMargin={adjustMargin}>Headline (legacy)</Headline>
        <Subheading2 adjustMargin={adjustMargin}>
          Subheading2 (legacy)
        </Subheading2>
        <Subheading1 adjustMargin={adjustMargin}>
          Subheading1 (legacy)
        </Subheading1>
        <Title adjustMargin={adjustMargin}>Title (legacy)</Title>
      </Fragment>
    );
  });
