var minimist = require('minimist');

/** 
  * Allows to pass in the theme name to use in Less build as a cmd line argument. 
  * Works with any script, for instance `npm start -- --theme dark`
  */
module.exports = function() {
  var argv = minimist(process.argv.slice(2));
  var args = {};
  args.globalVars = { theme: argv.theme || 'cdk' };
  return args;
};
