'use strict';

var path = require('path');
var npmbin = require('./utils/npmbin');

var cmd = 'lint-staged';
var args = ['--config', path.join(__dirname, '../lintstagedrc')];

npmbin(cmd, args, function(code) {
  if (code !== 0) {
    console.error('`' + cmd + ' ' + args.join(' ') + '` failed');
    process.exit(1);
  }
});
