'use strict';

var minimist = require('minimist');
var path = require('path');

/**
  * Loads a file passed in as a named cmd line argument
  * For instance `npm start -- --middleware ./api/mocks.js`
  */
module.exports = function(name) {
  var argv = minimist(process.argv.slice(2));
  var file = argv[name];
  return file ? require(path.resolve(file)) : null;
};
